import React from 'react'
import './style.css'

class PopUpBuyNow extends React.Component {
    constructor(props) {
        super(props);
        this.setState = {
            isChoose: ''
        }
        this.closeFopUpForm = this.closeFopUpForm.bind(this);
    }

    areaClick = (isChoose) =>{
        this.setState({
            choose: isChoose
        })
    }

    closeFopUpForm() {
        this.props.onClose();
    }

    render() {
        const {choose} = this.state;
        return (
            <React.Fragment>
                <div className="popup-form">
                    <p className="text">
                        Bạn muốn nhận hàng ở khu vực nào?
                </p>
                    <i className="ion-android-close ionic-close" onClick={this.closeFopUpForm} />
                    <div className="dropdown">
                        <button className="dropbtn">--Chọn khu vực--</button>
                        <div className="dropdown-content">
                            <a href="#">Quận 1</a>
                            <a href="#">Quận 2</a>
                            <a href="#">Quận 3</a>
                        </div>
                    </div>
                    <div className="button-watch button-text" onClick={this.closeFopUpForm}>
                        <div>Xem ngay</div>
                    </div>
                </div>
                <div className="backdrop"/>
            </React.Fragment>
        )
    }
}

export default PopUpBuyNow;
