import React from 'react'
import './style.css'
import ProductDetail from '../../Button/ProductDetail';

class DailyProducts extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoad: false,
            items: []
        }
    }

    componentDidMount() {
        fetch("https://nguyenhuulinh.github.io/listproducts.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoad: true,
                        items: result
                    });
                    // console.log(result)
                }
            )
        // .catch((error) => {
        //     this.setState({
        //         isLoad:true,
        //         items =null
        //     })
        // }
        // )
    }

    render() {
        const { error, isLoad, items } = this.state;
        if (!isLoad) {
            return <div>Loading...</div>
        }
        else {
            return (
                <div className="cover">
                    {items.map(item => (
                        <div>
                            <div className="cover-img">
                                <img src={item.url_img} className="img-img" />
                            </div>
                            <div className="cover-icon">
                                <img src={item.url_icon} className="img-icon" />
                            </div>
                            <div className="cover-content">
                                {item.title}
                            </div>
                            <div >
                                {item.content}
                            </div>
                        </div>
                    )
                    )
                    }

                </div>
            )
        }
    }
}

export default DailyProducts;
