import React from 'react';
import './style.css'

class BuyNow extends React.Component {
    constructor(props) {
        super(props)
        this.state= {
            showBuyPopupForm: false
        }
        this.handleFormBuy= this.handleFormBuy.bind(this);
    }

    handleFormBuy() {
        this.props.onClikBuy
    }

    render() {
        return (
            <div>
                <div className="button-buy button-text" onClick={this.handleFormBuy}>
                    Mua ngay
                </div>
                <div className="button-buy button-text">
                    Đến cửa hàng
                </div>
            </div>
        )
    }
}

export default BuyNow;
